class CemMailUsers extends BaseCemMailUsers
{
	public function setUp()
    { 
        parent::setUp();
        $this->actAs('Timestampable');
    }
	/**
	 * function updateMailStatus
	 * @todo Function update cemetery mail users tables fields
	 * @static
	 * @param $snIdGrantee = Grantee ID
	 */
	public static function updateMailStatus($ssFieldName,$anIds)
	{
		$oQuery = Doctrine_Query::create()
				->update('CemMailUsers')
				->set($ssFieldName,'?',1)
				->whereIn('id', $anIds);

		$oQuery->execute();
	}
	public static function saveSendMailDetails($amDetails)
	{
		$oCemMailUsers = new CemMailUsers();
		$oCemMailUsers->setCemMailId($amDetails['snMailId']);
		$oCemMailUsers->setFromUserId($amDetails['snFromUserId']);
		$oCemMailUsers->setToUserId($amDetails['snToUserId']);
		$oCemMailUsers->setSentStatus($amDetails['bSentStatus']);
		$oCemMailUsers->setReadUnreadStatus(0);
		$oCemMailUsers->setDeleteStatus(0);
		$oCemMailUsers->setMailSendStatus(0);
		
		$oCemMailUsers->save();
	}
}
